<script src="http://ajax.googleapis.com/ajax/libs/jquery/1.10.2/jquery.min.js"></script>
<script>
$(document).ready(function(){
$("#field_search").click(function(e){
var e=window.event||e;

$('#btnsearch').fadeIn(1000, function() {
$('#btnsearch').css("opacity","1");
});

$('#search').addClass("searchexpanded");
$('#field_search').addClass("field_searchexpanded");
$('#btnsearch').addClass("cursor");

$(this).addClass("hover");
e.stopPropagation();
});

$(document).click(function(e){

$('#search').removeClass("searchexpanded");
$('#field_search').removeClass("field_searchexpanded");

$('#btnsearch').fadeIn(1000, function() {
$('#btnsearch').css("opacity","0");
});

}); 

$("#field_search").focusout(function(){
	
$('#search').removeClass("searchexpanded");
$('#field_search').removeClass("field_searchexpanded");
$('#btnsearch').attr("disabled",false);
$('#btnsearch').removeClass("cursor");

$('#btnsearch').fadeIn(1000, function() {
$('#btnsearch').css("opacity","0");
});
});
$("#field_search").focus(function(){
$('#btnsearch').fadeIn(1000, function() {
$('#btnsearch').css("opacity","1");
});

$('#search').addClass("searchexpanded");
$('#field_search').addClass("field_searchexpanded");
});


});

</script>

<script type="text/javascript" src="/js/ajax-jquery-1.9.1.min.js"></script>
<script src="/js/jquery-1.4.1.min.js"></script>
<script type="text/javascript">
$(document).ready(function(){
$(function(){
$(".fsearch").keyup(function() 
{ 

var field_search = $(this).val();
var dataString = 'search='+ field_search;
if(field_search!='')
{
    $.ajax({
    type: "POST",
    url: "search-ajax-kace.php",
    data: dataString,
    cache: true,
	timeout: 1000,
    success: function(html)
    {
		

	$("#result").html('<div class="loading"><img src="data:image/png;base64,"></div>');



  $("#result").html(html).fadeIn().show();

    }
	
    });
	 
}return false;    
});
$(".fsearch").keyup(function() 
{ 
    jQuery("#result").fadeIn();
});
});
});
</script>



 